\draw (1,0) node[anchor=center] {\strut \texttt{$\div$}} ++ (-0.3,-0.3) rectangle ++ (0.6,0.6); 
\draw (2,0) node[anchor=center] {\strut \texttt{$2$}} ++ (-0.3,-0.3) rectangle ++ (0.6,0.6); 
\draw (3,0) node[anchor=center] {\strut \texttt{$\sqrt{\,\,}$}} ++ (-0.3,-0.3) rectangle ++ (0.6,0.6); 
\draw (4,0) node[anchor=center] {\strut \texttt{$2$}} ++ (-0.3,-0.3) rectangle ++ (0.6,0.6); 
\draw (5,0) node[anchor=center] {\strut \texttt{$=$}} ++ (-0.3,-0.3) rectangle ++ (0.6,0.6); 
\end{tikzpicture}
